import React from 'react';
import './App.css';
import {BrowserRouter as Router, Link} from 'react-router-dom'; 
import {Container, Navbar} from 'react-bootstrap';
import nav from 'react-bootstrap/Navbar';
import Footer from './components/Footer';
import home from './pages/homePage'



class App extends React.Component {
  constructor(props){
    super(props);
    this.state ={
      title:"Ntombizodwa Mafa",
      headerLinks:[
        {title:'Name', path: '/'},
        {title:'About', path: '/about'},
        {title:'Contact', path: '/contact'}
      ],
      home:{
        title:'Ntombizodzwa Mafa',
        subtitle:'Cirriculum Vatue',
        text: 'Check out my projects'
      },
      contact:{
        title:'Feel free to contact me'
      },
      about:{
        title:'About Me'
      }
    }
  }

  render(){
    return (
      <Router>
        <Container class="p-0" fluid={true}>
          <Navbar className="border-buttom" bg="transparent" expand="lg">
            <Navbar.Brand>Ntombizodzwa Mafa</Navbar.Brand>
            <Navbar.Toggle className="border-0" aria-controls="navbar-toggle"/>
            <Navbar.Collapse id="navbar-toogle">
            <nav className="ml-auto">
                <Link className="nav-link" to="/">home</Link>
                <Link className="nav-link" to="/about">About</Link>
                <Link className="nav-link" to="/contact">Contact</Link>

                <Route path="/" exact render={() => home}/>
            </nav>
            </Navbar.Collapse>
          </Navbar>

         <Footer />


        </Container>
      </Router>
    );
  }

}

export default App;
