import React from 'react';
import Content from '../components/content';
import Hero from '../components/hero';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

class ContactPage extends React.Component{
    constructor(props){
        super(props);
        this.state={
            name:"",
            email:"",
            message:'',
            emailSent:null,
        }
    }

    handleChange = ()=>{

    }
    
    render(){
        return(
            <div>
                <Hero title={this.props.title}/>

                <Content>
                    <Form onSubmit={this.handleSubmit}>
                        <Form.Group>
                            <Form.Label htmlFor="full-name">Full Name</Form.Label>
                            <Form.Control id="full-name" name="name" type="text" value={this.value.name} />
                        </Form.Group>

                        <Form.Group>
                            <Form.Label htmlFor="email">Email</Form.Label>
                            <Form.Control id="email" name="email" type="email" value={this.value.email} />
                        </Form.Group>

                        <Form.Group>
                            <Form.Label htmlFor="message">Message</Form.Label>
                            <Form.Control id="message" name="message" as="textarea"  rows={3} value={this.value.message} />
                        </Form.Group>

                        <Button className="d-inline-block" variant="primary" type="submit">
                            Send
                        </Button>

                        {this.state.emailSent === true && <p className="d-inline-sucess-msg">Email Sent</p>}
                        {this.state.emailSent === false && <p className="d-inline-sucess-msg">Email Not sent</p>}
                    </Form>
                </Content>
            </div>
        );
    }

}

export default ContactPage;
