import React from 'react';
import Content from '../components/content';
import Hero from '../components/hero';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import { db } from '../firebase';

class ContactPage extends React.Component{
    constructor(props){
        super(props);
        this.state={
            name:'',
            email:'',
            message:'',
            disabled: false,
            emailSent:null
        }
    }

    /*handleChange = ()=>{

    }*/

    handleSubmit = (event) =>{
        event.preventDefault(); //to avoid refreshing
        this.setState={
            disabled:true
        }

        db.collection('ContactsPage').add({
            name : this.state.name,
            email : this.state.email,
            message : this.state.message,
        }).then( () => {
            alert('Message has been submited. Thank you!');
        }).catch( (error) => {
            alert(error.message);
        });

        this.setState={
            name:'',
            email:'',
            message:'',
        }
    }
    
    render(){
        return(
            <div>
                <Hero title={this.props.title}/>

                <Content>
                    <Form onSubmit={this.handleSubmit}>
                        <Form.Group>
                            <Form.Label htmlFor="full_name">Full Name</Form.Label>
                            <Form.Control id="full_name" name="name" type="text" value={this.state.name} onChange={this.handleChange} />
                        </Form.Group>

                        <Form.Group>
                            <Form.Label htmlFor="email">Email</Form.Label>
                            <Form.Control id="email" name="email" type="email" value={this.state.email} />
                        </Form.Group>

                        <Form.Group>
                            <Form.Label htmlFor="message">Message</Form.Label>
                            <Form.Control id="message" name="message" as="textarea"  rows="3" value={this.state.message} />
                        </Form.Group>

                        <Button className="d-inline-block" variant="primary" type="submit">
                            Send
                        </Button>

                        {this.state.emailSent === true && <p className="d-inline-sucess-msg">Email Sent</p>}
                        {this.state.emailSent === false && <p className="d-inline-sucess-msg">Email Not sent</p>}
                    </Form>
                </Content>
            </div>
        );
    }

}

export default ContactPage;
