import React from 'react';
import './App.css';
import {BrowserRouter as Router,Route, Link} from 'react-router-dom'; 
import {Container, Navbar} from 'react-bootstrap';
import Footer from './components/Footer';

import homePage from './pages/homePage';
import AboutPage from './pages/AboutPage';
import ContactPage from './pages/ContactPage';



class App extends React.Component {
  constructor(props){
    super(props);
    this.state ={
      title:"Ntombizodwa Mafa",
      headerLinks:[
        {title:'Name', path: '/'},
        {title:'About', path: '/about'},
        {title:'Contact', path: '/contact'}
      ],
      home:{
        title:'Ntombizodzwa Mafa',
        subtitle:'Cirriculum Vatue',
        text: 'Check out my projects'
      },
      contact:{
        title:'Feel free to contact me'
      },
      about:{
        title:'About Me'
      }
    }
  }

  render(){
    return (
      <Router>
        <Container class="p-0" fluid={true}>
          <Navbar className="border" bg="transparent" expand="lg">
            <Navbar.Brand>Ntombizodzwa Mafa</Navbar.Brand>
            <Navbar.Toggle className="border-0" aria-controls="navbar-toggle"/>
            <Navbar.Collapse id="navbar-toogle">
            <nav className="ml-auto">
                <Link className="nav-link" to="/">Home</Link>
                <Link className="nav-link" to="/about">About</Link>
                <Link className="nav-link" to="/contact">Contact</Link>

              </nav>
            </Navbar.Collapse>
          </Navbar>


        <Route path="/" exact render={() => <home title={this.state.home.title} subtitle={this.state.home.subtitle} text={this.state.home.text} />}/>


        <Route path="/contact" exact render={() => <AboutPage title={this.state.about.title} />}/>
        <Route path="/contact" render={() => <ContactPage title={this.state.contact.title} />} />  

         <Footer />


        </Container>
      </Router>
    );
  }

}

export default App;
